import React from 'react';
import './Card.css';
import SlideShow from './SlideShow';
import cristiano_ronaldo from './images/cristiano_ronaldo.jpg';
import barack_obama from './images/barack_obama.jpg';
import gianni_versace from './images/gianni_versace.jpg';
import lady_gaga from './images/lady_gaga.jpg';
import mother_teresa from './images/mother_teresa.jpg';
import muhammad_ali from './images/muhammad_ali.jpg';
import neil_armstrong from './images/neil_armstrong.jpg';
import richard_branson from './images/richard_branson.jpg';
import sarah_blakely from './images/sarah_blakely.jpg';
import steve_jobs from './images/steve_jobs.jpg';
import tom_brady from './images/tom_brady.jpg';
import viola_davis from './images/viola_davis.jpg';

const images = [cristiano_ronaldo, barack_obama, gianni_versace,
				lady_gaga, mother_teresa, muhammad_ali, neil_armstrong, 
				richard_branson, sarah_blakely, steve_jobs, tom_brady,
				viola_davis];

const imageClick = () => {
	/*return(
		<SlideShow
			interval={3000}
			images={[cristiano_ronaldo, barack_obama, gianni_versace]}
		/>
	)*/

}

const Card = ({name, username, id}) => {

	return(
		<div className='bg-washed-blue dib br3 pa3 ma2 grow bw2 shadow-5 tc'>
			<img alt='robots' className='photo' src={images[id-1]} onClick={imageClick()} />
			<div>
				<h2 className='name'>{name}</h2>
				<p className='username'>{username}</p>
			</div>
		</div>
	);
}

export default Card;
