import React, {useState} from 'react';
import Card from './Card';

const CardList = ({ robots }) => {

	const parentState = this.props.state;

	const state = {image_clicked : false}

	const getInitialState = () => {return state}

	const changeState = (new_state) => {this.state = new_state}

	if (this.state === true){
		this.props.changeState({ slideShow: true })
	}

	const cardComponent = robots.map((user, i) => {
		return ( 
			<Card 
				key={i} 
				id={robots[i].id} 
				name={robots[i].name} 
				username={robots[i].username}
			/>
		);
	});
	return (
		<div>
			{ cardComponent }
	    </div>
	);
}

export default CardList;
