import React from 'react';
import './Card.css';

const Card = ({name, username, image, id}) => {
	return(
		<div className='bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5 tc'>
			<img alt='robots' className='photo' src={image} />
			<div>
				<h2>{name}</h2>
				<p>{username}</p>
			</div>
		</div>
	);
}

export default Card;
