import React from 'react';
import './Card.css';
import './Images.js';
import cristiano_ronaldo from './cristiano_ronaldo.jpg';
import barack_obama from './barack_obama.jpg';
import gianni_versace from './gianni_versace.jpg';
import lady_gaga from './lady_gaga.jpg';
import mother_teresa from './mother_teresa.jpg';
import muhammad_ali from './muhammad_ali.jpg';
import neil_armstrong from './neil_armstrong.jpg';
import richard_branson from './richard_branson.jpg';
import sarah_blakely from './sarah_blakely.jpg';
import steve_jobs from './steve_jobs.jpg';
import tom_brady from './tom_brady.jpg';
import viola_davis from './viola_davis.jpg';

const images = [cristiano_ronaldo,
				barack_obama,
				gianni_versace,
				lady_gaga,
				mother_teresa,
				muhammad_ali,
				neil_armstrong,
				richard_branson,
				sarah_blakely,
				steve_jobs,
				tom_brady,
				viola_davis];

const info = [images[1]];

let rotated = false;

const Card = ({name, username, id}) => {

	const clicked = () => {
		let div = document.getElementById(id), angle = rotated ? 0 : 180;
		div.style.webkitTransform = 'rotate(' + angle + 'deg)';
		div.style.mozTransform    = 'rotate('+ angle +'deg)'; 
	    div.style.msTransform     = 'rotate('+ angle +'deg)'; 
	    div.style.oTransform      = 'rotate('+ angle +'deg)'; 
	    div.style.transform       = 'rotate('+ angle +'deg)';

	    let current = images[div.id-1];
		let next = info[div.id-1];

	    if (rotated === false){
	    	div.src = next;
	    	div.height = 350;
	    	div.width = 350;
	    	rotated = true;
	    	return div.src
	    }else{
	    	div.src = current;
	    	div.height = 250;
	    	div.width = 250;
	    	rotated = false;
	    	return div.src;
	    }
    }

	return(
		<div className='bg-washed-blue dib br3 pa3 ma2 grow bw2 shadow-5 tc'>
			<img  alt='robots' id={id} width={250} height={250} className='photo' src={images[id-1]} onClick={clicked} />
			<div>
				<h2 className='name'>{name}</h2>
				<p className='username'>{username}</p>
			</div>
		</div>
	);
}

export default Card;
